require 'rspec/autorun'  # => true
require 'rspec'          # => true

MAP = {
  pedra:   :tesoura,  # => :tesoura
  tesoura: :papel,    # => :papel
  papel:   :pedra     # => :pedra
}                     # => {:pedra=>:tesoura, :tesoura=>:papel, :papel=>:pedra}

def julgar(*jogadores)
  if jogadores[0] == jogadores[1]                # => true, true, true, false, false, false, false, false, false
    julgar_iguais                                # => "Empate", "Empate", "Empate"
  elsif jogadores[0] != jogadores[1]             # => true, true, true, true, true, true
    ganhador = julgar_diferentes(jogadores) + 1  # => 1, 2, 2, 1, 1, 2
    "Jogador #{ganhador} ganhou!"                # => "Jogador 1 ganhou!", "Jogador 2 ganhou!", "Jogador 2 ganhou!", "Jogador 1 ganhou!", "Jogador 1 ganhou!", "Jogador 2 ganhou!"
  end                                            # => "Empate", "Empate", "Empate", "Jogador 1 ganhou!", "Jogador 2 ganhou!", "Jogador 2 ganhou!", "Jogador 1 ganhou!", "Jogador 1 ganhou!", "Jogador 2 ganhou!"
end

def julgar_iguais
  'Empate'         # => "Empate", "Empate", "Empate"
end

def julgar_diferentes(jogadores)
  MAP[jogadores[0]] == jogadores[1] ? 0 : 1  # => 0, 1, 1, 0, 0, 1, 0, 1, 1, 0
end

